import * as types from '../constants/actions';
import React from 'react';

const initState = {
  books: {

  },
  currentBook: {
    name: '',
    genre: '',
    author: {
      name: '',
      age: null,
      books: []
    }
  }
}

const eventReducer = (state=initState, action) => {
  switch(action.type) {
    case(types.AllBooks):
      console.log('in all books')
      const AllBooksState = Object.assign({}, state);

    return AllBooksState;

    case(types.SingleBook):
      // console.log('in single book ', action.currentBook)
      console.log('state ', state)
      const SingleBookState = Object.assign({}, state)
      SingleBookState.name = action.currentBook.name;
      SingleBookState.genre = action.currentBook.genre;

    return SingleBookState;

    default:
      return state;
  }
}

export default eventReducer;
