const AllBooks = 'AllBooks';
const SingleBook = 'SingleBooks';

const allBooks = (allBooks) => ({type: AllBooks, allBooks});
const singleBook = (currentBook) => ({type: SingleBook, currentBook});

module.exports = {
  AllBooks,
  allBooks,
  SingleBook,
  singleBook
}
