import { gql } from 'apollo-boost';

const getBookQuery = gql`
  {
    books {
      name
      id
    }
  }
`

const getAuthorsQuery = gql`
  {
    authors {
      name
      id
    }
  }
`

const addBookMutation = gql`
  mutation($name: String!, $genre: String!, $authorId: ID!) {
    addBook(name: $name, genre: $genre, authorId: $ID) {
      name
      genre
    }
  }
`

export {
  getBookQuery,
  getAuthorsQuery,
  addBookMutation
}
